package com.example.thesis.object;

public class SingleRowWithImage {
	int resid;
	String text;
	
	public int getResid() {
		return resid;
	}
	public void setResid(int resid) {
		this.resid = resid;
	}
	public String getText() {
		return text;
	}
	public void setText(String text) {
		this.text = text;
	}
	
	public SingleRowWithImage() {
		// TODO Auto-generated constructor stub
		this.resid = 0;
		this.text = null;
	}
}
